function checkRoleSession($roleid, $page)
{
    $array_1 = array('', 'dashboard', 'users', 'instructors', 'majors', 'students', 'roles', 'learning_moduls', 'allreports');
    $array_2 = array('', 'dashboard', 'instructors', 'majors', 'students', 'roles', 'learning_moduls', 'allreports');
    $array_3 = array('', 'dashboard', 'instructors', 'learning_moduls');
    $array_4 = array('', 'dashboard', 'learning_moduls');
    $array_5 = array('', 'dashboard', 'learning_moduls');
    for ($i = 1; $i <= 5; $i++) {
        // foreach ($roleid as $rowr) {
        if ($roleid == $i) {
            if (!in_array($page, ${'array_' . $i . ''})) {
                header('Location: http://localhost/modul/pageerror.php');
                die();
            } else {
                return ${'array_' . $i . ''};
            }
        }
        // }
    }
}

function checkMenuRole($roleid, $page)
{
    $array_2 = array('users');
    $array_3 = array('users', 'majors', 'students', 'roles', 'allreports');
    $array_4 = array('users', 'instructors', 'majors', 'students', 'roles',  'allreports');
    $array_5 = array('users', 'instructors', 'majors', 'students', 'roles',  'allreports');
    $nom = '';
    if (in_array($roleid, [4, 5])) {
        $nom .= '$("#headingmasters").css("display","none");';
        $nom .= '$("#headingreports").css("display","none");';
        $nom .= '$("#masters").css("display","none");';
        $nom .= '$("#reports").css("display","none");';
    }
    if (in_array($roleid, [3])) {
        $nom .= '$("#headingreports").css("display","none");';
        $nom .= '$("#reports").css("display","none");';
    }
    for ($i = 2; $i <= 5; $i++) {
        // foreach ($roleid as $rowr) { 
        if ($roleid == $i) {
            foreach (${'array_' . $i . ''} as $asd) {
                $nom .= '$("#' . $asd . '").css("display","none");';
            }
        }
        // }
    }

    return $nom;
}
